const currentLocalDate=new Date();var mainText=document.getElementById("mainText");mainText.querySelector(":nth-child(1)").innerHTML="There is";if(currentLocalDate.getMonth()==10-1){var countdownTo=new Date(currentLocalDate.getFullYear(),10-1,31);mainText.querySelector(":nth-child(2)").innerHTML=Math.floor((countdownTo.getTime()-currentLocalDate.getTime())/(1000*60*60*24)+1);mainText.querySelector(":nth-child(2)").innerHTML+=" day";if(currentLocalDate.getDate()!=30){mainText.querySelector(":nth-child(2)").innerHTML+="s"}mainText.querySelector(":nth-child(3)").innerHTML="to the last day of October."}else{var countdownToYear=currentLocalDate.getFullYear();if((currentLocalDate.getMonth()+1)>10){countdownToYear++}var countdownTo=new Date(countdownToYear,10-1,1,0,0,0);var daysLeft=Math.floor((countdownTo.getTime()-currentLocalDate.getTime())/(1000*60*60*24))+1;mainText.querySelector(":nth-child(2)").innerHTML=daysLeft;mainText.querySelector(":nth-child(2)").innerHTML+=" day";if(daysLeft!=1){mainText.querySelector(":nth-child(2)").innerHTML+="s"}mainText.querySelector(":nth-child(3)").innerHTML="to the first day of October."};